import React, { useState } from 'react';
import { Send, Hash, Users, Circle, Smile, AtSign, Search, Plus } from 'lucide-react';
import { TeamChatChannel, TeamMember, TeamChatMessage } from '../types';
import { mockTeamChatChannels, mockTeamMembers } from '../data/mockData';

const TeamChat: React.FC = () => {
  const [selectedChannel, setSelectedChannel] = useState<TeamChatChannel>(mockTeamChatChannels[0]);
  const [newMessage, setNewMessage] = useState('');
  const [showMentions, setShowMentions] = useState(false);
  const [mentionQuery, setMentionQuery] = useState('');

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'online': return 'text-green-500';
      case 'away': return 'text-yellow-500';
      case 'offline': return 'text-gray-400';
      default: return 'text-gray-400';
    }
  };

  const getChannelIcon = (type: string) => {
    switch (type) {
      case 'general': return '#';
      case 'support': return 'ðŸŽ§';
      case 'development': return 'ðŸ’»';
      case 'marketing': return 'ðŸ“ˆ';
      default: return '#';
    }
  };

  const formatTime = (date: Date) => {
    return new Intl.DateTimeFormat('en-GB', {
      hour: '2-digit',
      minute: '2-digit'
    }).format(date);
  };

  const formatDate = (date: Date) => {
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === today.toDateString()) {
      return 'Today';
    } else if (date.toDateString() === yesterday.toDateString()) {
      return 'Yesterday';
    } else {
      return date.toLocaleDateString('en-GB', { day: '2-digit', month: 'short' });
    }
  };

  const handleMessageChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    const value = e.target.value;
    setNewMessage(value);

    // Check for @ mentions
    const lastAtIndex = value.lastIndexOf('@');
    if (lastAtIndex !== -1 && lastAtIndex === value.length - 1) {
      setShowMentions(true);
      setMentionQuery('');
    } else if (lastAtIndex !== -1) {
      const afterAt = value.substring(lastAtIndex + 1);
      if (!afterAt.includes(' ')) {
        setShowMentions(true);
        setMentionQuery(afterAt);
      } else {
        setShowMentions(false);
      }
    } else {
      setShowMentions(false);
    }
  };

  const handleMentionSelect = (member: TeamMember) => {
    const lastAtIndex = newMessage.lastIndexOf('@');
    const beforeAt = newMessage.substring(0, lastAtIndex);
    const newValue = beforeAt + `@${member.email} `;
    setNewMessage(newValue);
    setShowMentions(false);
  };

  const filteredMembers = mockTeamMembers.filter(member =>
    member.name.toLowerCase().includes(mentionQuery.toLowerCase()) ||
    member.email.toLowerCase().includes(mentionQuery.toLowerCase())
  );

  const renderMessage = (message: TeamChatMessage) => {
    let messageText = message.message;

    // Highlight mentions
    message.mentions.forEach(mentionId => {
      const member = mockTeamMembers.find(m => m.id === mentionId);
      if (member) {
        messageText = messageText.replace(
          `@${member.email}`,
          `<span class="bg-blue-100 text-blue-800 px-1 rounded">@${member.name}</span>`
        );
      }
    });

    return (
      <div className="flex space-x-3 p-4 hover:bg-gray-50">
        <img
          src={message.sender.avatar || `https://ui-avatars.com/api/?name=${message.sender.name}&background=3b82f6&color=fff`}
          alt={message.sender.name}
          className="w-10 h-10 rounded-full"
        />
        <div className="flex-1">
          <div className="flex items-center space-x-2 mb-1">
            <span className="font-semibold text-gray-900">{message.sender.name}</span>
            <span className="text-sm text-gray-500">{message.sender.role}</span>
            <span className="text-xs text-gray-400">{formatTime(message.timestamp)}</span>
            {message.edited && (
              <span className="text-xs text-gray-400">(edited)</span>
            )}
          </div>
          <div 
            className="text-gray-700 leading-relaxed"
            dangerouslySetInnerHTML={{ __html: messageText }}
          />
          {message.reactions && message.reactions.length > 0 && (
            <div className="flex space-x-2 mt-2">
              {message.reactions.map((reaction, index) => (
                <button
                  key={index}
                  className="flex items-center space-x-1 bg-gray-100 hover:bg-gray-200 rounded-full px-2 py-1 text-sm"
                >
                  <span>{reaction.emoji}</span>
                  <span className="text-gray-600">{reaction.users.length}</span>
                </button>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="h-full flex bg-white rounded-lg shadow-sm border border-gray-200">
      {/* Channels Sidebar */}
      <div className="w-64 border-r border-gray-200 flex flex-col">
        <div className="p-4 border-b border-gray-200">
          <h2 className="text-lg font-semibold text-gray-900 mb-3">Team Chat</h2>
          <button className="w-full btn-primary flex items-center justify-center gap-2">
            <Plus className="w-4 h-4" />
            New Channel
          </button>
        </div>

        {/* Channels List */}
        <div className="flex-1 overflow-y-auto">
          <div className="p-4">
            <h3 className="text-sm font-medium text-gray-500 uppercase tracking-wide mb-3">Channels</h3>
            <div className="space-y-1">
              {mockTeamChatChannels.map((channel) => (
                <button
                  key={channel.id}
                  onClick={() => setSelectedChannel(channel)}
                  className={`w-full flex items-center justify-between p-2 rounded-lg text-left transition-colors ${
                    selectedChannel.id === channel.id
                      ? 'bg-primary-50 text-primary-700'
                      : 'text-gray-600 hover:bg-gray-100'
                  }`}
                >
                  <div className="flex items-center space-x-2">
                    <span className="text-lg">{getChannelIcon(channel.type)}</span>
                    <span className="font-medium">{channel.name}</span>
                  </div>
                  {channel.unreadCount > 0 && (
                    <span className="bg-red-500 text-white text-xs rounded-full px-2 py-1 min-w-[20px] text-center">
                      {channel.unreadCount}
                    </span>
                  )}
                </button>
              ))}
            </div>
          </div>

          {/* Team Members */}
          <div className="p-4 border-t border-gray-200">
            <h3 className="text-sm font-medium text-gray-500 uppercase tracking-wide mb-3">Team Members</h3>
            <div className="space-y-2">
              {mockTeamMembers.map((member) => (
                <div key={member.id} className="flex items-center space-x-3 p-2 rounded-lg hover:bg-gray-50">
                  <div className="relative">
                    <img
                      src={member.avatar || `https://ui-avatars.com/api/?name=${member.name}&background=3b82f6&color=fff`}
                      alt={member.name}
                      className="w-8 h-8 rounded-full"
                    />
                    <Circle className={`w-3 h-3 absolute -bottom-1 -right-1 bg-white rounded-full ${getStatusColor(member.status)}`} fill="currentColor" />
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-gray-900 truncate">{member.name}</p>
                    <p className="text-xs text-gray-500 truncate">{member.role}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Chat Area */}
      <div className="flex-1 flex flex-col">
        {/* Chat Header */}
        <div className="p-4 border-b border-gray-200 bg-gray-50">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <span className="text-2xl">{getChannelIcon(selectedChannel.type)}</span>
              <div>
                <h3 className="font-semibold text-gray-900">{selectedChannel.name}</h3>
                <p className="text-sm text-gray-500">{selectedChannel.description}</p>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              <button className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100">
                <Search className="w-5 h-5" />
              </button>
              <button className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100">
                <Users className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>

        {/* Messages */}
        <div className="flex-1 overflow-y-auto">
          <div className="divide-y divide-gray-100">
            {selectedChannel.messages.map((message) => (
              <div key={message.id}>
                {renderMessage(message)}
              </div>
            ))}
          </div>
        </div>

        {/* Message Input */}
        <div className="p-4 border-t border-gray-200 bg-white">
          <div className="relative">
            <textarea
              value={newMessage}
              onChange={handleMessageChange}
              placeholder={`Message #${selectedChannel.name}`}
              className="w-full p-3 border border-gray-300 rounded-lg resize-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              rows={3}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  // Handle send message
                  setNewMessage('');
                }
              }}
            />

            {/* Mentions Dropdown */}
            {showMentions && (
              <div className="absolute bottom-full left-0 w-64 bg-white border border-gray-200 rounded-lg shadow-lg mb-2 max-h-48 overflow-y-auto">
                <div className="p-2 border-b border-gray-100">
                  <p className="text-xs font-medium text-gray-500 uppercase tracking-wide">Mention someone</p>
                </div>
                {filteredMembers.map((member) => (
                  <button
                    key={member.id}
                    onClick={() => handleMentionSelect(member)}
                    className="w-full flex items-center space-x-3 p-3 hover:bg-gray-50 text-left"
                  >
                    <img
                      src={member.avatar || `https://ui-avatars.com/api/?name=${member.name}&background=3b82f6&color=fff`}
                      alt={member.name}
                      className="w-8 h-8 rounded-full"
                    />
                    <div>
                      <p className="font-medium text-gray-900">{member.name}</p>
                      <p className="text-sm text-gray-500">{member.role}</p>
                    </div>
                  </button>
                ))}
              </div>
            )}

            <div className="flex items-center justify-between mt-3">
              <div className="flex items-center space-x-2">
                <button className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100">
                  <AtSign className="w-5 h-5" />
                </button>
                <button className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100">
                  <Smile className="w-5 h-5" />
                </button>
              </div>
              <button 
                className="btn-primary flex items-center gap-2"
                disabled={!newMessage.trim()}
              >
                <Send className="w-4 h-4" />
                Send
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TeamChat;
